'use strict';

var _require = require('../../lifx'),
    utils = _require.utils;

var Packet = {
  size: 5
};

/**
 * Converts packet specific data from a buffer to an object
 * @param  {Buffer} buf Buffer containing only packet specific data no header
 * @return {Object}     Information contained in packet
 */
Packet.toObject = function (buf) {
  var obj = {};
  var offset = 0;

  if (buf.length !== this.size) {
    throw new Error('Invalid length given for setHevCycle LIFX packet');
  }

  obj.enable = buf.readUInt8(offset);
  offset += 1;
  obj.duration = buf.readUInt32LE(offset);
  offset += 4;

  return obj;
};

/**
 * Converts the given packet specific object into a packet
 * @param  {Object} obj object with configuration data
 * @return {Buffer}     packet
 */
Packet.toBuffer = function (obj) {
  var buf = Buffer.alloc(this.size);
  buf.fill(0);
  var offset = 0;

  buf.writeUInt8(obj.enable, offset);
  offset += 1;
  buf.writeUInt32LE(obj.duration, offset);
  offset += 4;

  return buf;
};

module.exports = Packet;